package ch03.sec10;

public class AssignmentOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int ans = 0;
		ans += 10;
		System.out.println("ans= " + ans);
		ans -= 5;
		System.out.println("ans= " + ans);
		ans *= 3;
		System.out.println("ans= " + ans);
		ans /= 5;
		System.out.println("ans= " + ans);
		ans %= 3;
		System.out.println("ans= " + ans);
	}

}
